CREATE OR REPLACE FUNCTION pls_obter_dados_estip_web ( NR_SEQ_ESTIPULANTE_WEB_P integer, IE_TIPO_P text) RETURNS varchar AS $body$
DECLARE

				 
ds_retorno_w			varchar(60) := '';
/* 
 Tipos 
 NE - Nome prestador 
 NU - Nome usu√°rio web 
 LG - Logo do usuario 
*/
 
 

BEGIN 
 
if (ie_tipo_p = 'NE') then 
	select	coalesce(max(obter_nome_pf(cd_pessoa_fisica)),'') 
	into STRICT	ds_retorno_w 
	from	pls_estipulante_web 
	where	nr_sequencia	= nr_seq_estipulante_web_p;
elsif (ie_tipo_p = 'NU') then 
	select	coalesce(max(nm_usuario_web),'') 
	into STRICT	ds_retorno_w 
	from	pls_estipulante_web 
	where	nr_sequencia	= nr_seq_estipulante_web_p;
elsif (ie_tipo_p = 'LG') then 
	select	coalesce(max(ds_diretorio_logo),'') 
	into STRICT	ds_retorno_w 
	from	pls_estipulante_web 
	where	nr_sequencia	= nr_seq_estipulante_web_p;
end if;
 
return	ds_retorno_w;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
