SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION pls_obter_dados_execucao ( nr_seq_execucao_p bigint, ie_tipo_p text) RETURNS varchar AS $body$
DECLARE

/*++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++
Finalidade:   Obter dados da execução da requisição
----------------------------------------------------------------------------------------------------------------------------------------------------
Locais de chamada direta:
[  ]  Objetos do dicionário [ x ] Tasy (Delphi/Java) [ x ] Portal [  ] Relatórios [ ] Outros:
 ----------------------------------------------------------------------------------------------------------------------------------------------------
Pontos de atenção:
++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++
	IE_TIPO_P

	NP - Nome do Prestador executor
*/ds_retorno_w		varchar(255);


BEGIN
if (nr_seq_execucao_p IS NOT NULL AND nr_seq_execucao_p::text <> '') then
	if (ie_tipo_p	= 'NP') then
		begin
			select	substr(pls_obter_dados_prestador(nr_seq_prestador, 'N'),1,255)
			into STRICT	ds_retorno_w
			from	pls_lote_execucao_req
			where	nr_sequencia	= (	SELECT	max(nr_seq_lote_exec)
							from	pls_execucao_requisicao
							where	nr_sequencia = nr_seq_execucao_p);
		exception
		when others then
			ds_retorno_w	:= '';
		end;
	end if;
end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
-- REVOKE ALL ON FUNCTION pls_obter_dados_execucao ( nr_seq_execucao_p bigint, ie_tipo_p text) FROM PUBLIC;

