CREATE OR REPLACE FUNCTION pls_obter_dados_faixa_etaria (nr_seq_faixa_etaria_p bigint, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE



/*	'D' - Descritivo
	'P' - Preço		*/
qt_idade_inicial_w	integer;
qt_idade_final_w	integer;
vl_preco_atual_w	double precision;
ds_retorno_w		varchar(255)	:= '';


BEGIN

select	qt_idade_inicial,
	qt_idade_final,
	vl_preco_atual
into STRICT	qt_idade_inicial_w,
	qt_idade_final_w,
	vl_preco_atual_w
from	pls_plano_preco
where	nr_sequencia	= nr_seq_faixa_etaria_p;

if (ie_opcao_p = 'D') then
	ds_retorno_w	:= qt_idade_inicial_w || ' à ' || qt_idade_final_w;
elsif (ie_opcao_p = 'P') then
	ds_retorno_w	:= to_char(vl_preco_atual_w);
end if;

return ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
