CREATE OR REPLACE FUNCTION pls_obter_dados_familia ( cd_familia_p bigint, nr_seq_contrato_p bigint, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE

 
/* +++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++ 
Finalidade: 
------------------------------------------------------------------------------------------------------------------- 
Locais de chamada direta: 
[ ] Objetos do dicionário [ ] Tasy (Delphi/Java) [ ] Portal [ ] Relatórios [ ] Outros: 
 ------------------------------------------------------------------------------------------------------------------ 
Pontos de atenção: 
 
ie_opcao_p 
 
	ST	Sequência do titular da família 
	NT	Nome do titular da família 
++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++ */
 
 
ds_retorno_w		varchar(255);
nr_seq_segurado_w	bigint;
nm_pessoa_fisica_w	varchar(255);

BEGIN
 
if (ie_opcao_p = 'ST') then 
	select	max(nr_sequencia) 
	into STRICT	nr_seq_segurado_w 
	from	pls_segurado 
	where	nr_seq_contrato		= nr_seq_contrato_p 
	and	cd_matricula_familia	= cd_familia_p;
	ds_retorno_w	:= nr_seq_segurado_w;
elsif (ie_opcao_p = 'NT') then 
	select	obter_nome_pf(max(cd_pessoa_fisica)) 
	into STRICT	nm_pessoa_fisica_w 
	from	pls_segurado 
	where	nr_seq_contrato		= nr_seq_contrato_p 
	and	cd_matricula_familia	= cd_familia_p;
	ds_retorno_w	:= nm_pessoa_fisica_w;
end if;
 
return	ds_retorno_w;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
