SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION pls_obter_dados_fat_pre_inter ( nr_seq_fatura_p bigint, ie_grafico_p bigint, ie_opcao_p text) RETURNS bigint AS $body$
DECLARE


/*
ie_grafico_p
1 - Quantidade de beneficiários com repasse
2 - Análise de valores cobrados X Regra de tabelas preços A800
*/
/*ie_opcao_p
I - Dados importados
C - Dados gerados pelo Tasy
*/
qt_retorno_w		bigint;
qt_total_benef_w	bigint;
qt_benef_tasy_w		bigint;
vl_tot_mensalidade_w	double precision;
vl_mens_gerado_w	double precision;


BEGIN

if (ie_grafico_p = 1) then
	select	max(QT_TOTAL_R802)
	into STRICT	qt_total_benef_w
	from	PTU_FATURA_PRE_TOTAL
	where	nr_seq_fatura	= nr_seq_fatura_p;

	if (coalesce(qt_total_benef_w::text, '') = '') then
		select	count(1)
		into STRICT	qt_total_benef_w
		from	PTU_FATURA_PRE_COBRANCA
		where	nr_seq_fatura	= nr_seq_fatura_p;
	end if;

	select	count(1)
	into STRICT	qt_benef_tasy_w
	from	PTU_FATURA_PRE_COBRANCA
	where	nr_seq_fatura	= nr_seq_fatura_p
	and	(NR_SEQ_SEGURADO IS NOT NULL AND NR_SEQ_SEGURADO::text <> '');

	if (ie_opcao_p	= 'I') then
		return qt_total_benef_w - qt_benef_tasy_w;
	elsif (ie_opcao_p	= 'C') then
		return qt_benef_tasy_w;
	end if;
elsif (ie_grafico_p = 2) then
	select	max(VL_TOTAL_MENSALIDADE)
	into STRICT	vl_tot_mensalidade_w
	from	PTU_FATURA_PRE_TOTAL
	where	nr_seq_fatura	= nr_seq_fatura_p;

	select	sum(VL_MENS_GERADO)
	into STRICT	VL_MENS_GERADO_w
	from	PTU_FATURA_PRE_COBRANCA
	where	nr_seq_fatura	= nr_seq_fatura_p
	and	(NR_SEQ_SEGURADO IS NOT NULL AND NR_SEQ_SEGURADO::text <> '');

	if (ie_opcao_p	= 'I') then
		return coalesce((vl_tot_mensalidade_w - coalesce(VL_MENS_GERADO_w,0)),0);
	elsif (ie_opcao_p	= 'C') then
		return coalesce(VL_MENS_GERADO_w,0);
	end if;
end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
