SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION pls_obter_dados_fornec_mat ( nr_seq_prest_fornec_p bigint, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE


/* +++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++
Finalidade:
-------------------------------------------------------------------------------------------------------------------
Locais de chamada direta:
[  ]  Objetos do dicionário [ ] Tasy (Delphi/Java) [  ] Portal [  ]  Relatórios [ ] Outros:
 ------------------------------------------------------------------------------------------------------------------
Pontos de atenção:
++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++ */
ds_retorno_w			varchar(255);
cd_cgc_w			varchar(255);
cd_fornecedor_w			bigint;


BEGIN

if (ie_opcao_p	= 'CD') then
	begin
		select	cd_cgc
		into STRICT	cd_cgc_w
		from	pls_prestador
		where	nr_sequencia = nr_seq_prest_fornec_p;
	exception
	when others then
		cd_cgc_w	:= null;
	end;

	if (cd_cgc_w IS NOT NULL AND cd_cgc_w::text <> '') then
		begin
			select	max(cd_fornecedor)
			into STRICT	cd_fornecedor_w
			from	pls_fornec_mat_fed_sc
			where	cd_cgc		= cd_cgc_w
			and	ie_situacao	= 'A';
		exception
		when others then
			cd_fornecedor_w	:= null;
		end;
	end if;

	ds_retorno_w	:= cd_fornecedor_w;
end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
-- REVOKE ALL ON FUNCTION pls_obter_dados_fornec_mat ( nr_seq_prest_fornec_p bigint, ie_opcao_p text) FROM PUBLIC;

