SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION pls_obter_dados_gerar_conta ( nr_seq_conta_p integer, ie_tipo_retorno_p text) RETURNS varchar AS $body$
DECLARE


/* IE_TIPO_RETORNO_P
	M - Médico executor
	G - Grau participação médico
	CBO - CBO Saúde
	CBOSOLIC - CBO Saúde do solicitante
	MS - Medico solicitante
*/
ds_retorno_w			varchar(10);
nr_crm_exec_imp_w		varchar(20);
uf_crm_exec_imp_w		varchar(2);
sg_conselho_exec_imp_w		varchar(20);
ie_funcao_executor_imp_w	varchar(10);
nr_seq_grau_partic_w		bigint;
cd_cbo_saude_exec_imp_w		varchar(30);
nr_crm_solic_imp_w		varchar(20);
uf_crm_solic_imp_w		varchar(2);
sg_conselho_solic_imp_w		varchar(20);
cd_cbo_saude_solic_imp_w	varchar(30);



BEGIN

select	nr_crm_exec_imp,
	uf_crm_exec_imp,
	sg_conselho_exec_imp,
	ie_funcao_executor_imp,
	cd_cbo_saude_exec_imp,
	nr_crm_solic_imp,
	uf_crm_solic_imp,
	cd_cbo_saude_solic_imp,
	sg_conselho_solic_imp
into STRICT	nr_crm_exec_imp_w,
	uf_crm_exec_imp_w,
	sg_conselho_exec_imp_w,
	ie_funcao_executor_imp_w,
	cd_cbo_saude_exec_imp_w,
	nr_crm_solic_imp_w,
	uf_crm_solic_imp_w,
	cd_cbo_saude_solic_imp_w,
	sg_conselho_solic_imp_w
from	pls_conta
where	nr_sequencia	= nr_seq_conta_p;

if (ie_tipo_retorno_p	= 'M') then
	/* Localizar médico executor da conta */

	select	coalesce(max(a.cd_pessoa_fisica),0)
	into STRICT	ds_retorno_w
	from	medico 			a,
		pessoa_fisica 		b,
		conselho_profissional	c
	where	a.cd_pessoa_fisica	= b.cd_pessoa_fisica
	and	b.nr_seq_conselho	= c.nr_sequencia
	and	a.nr_crm		= nr_crm_exec_imp_w
	and	a.uf_crm		= uf_crm_exec_imp_w
	and	c.sg_conselho		= sg_conselho_exec_imp_w;
elsif (ie_tipo_retorno_p	= 'G') then
	/* Obter o grau de participação do médico */

	select	max(nr_sequencia)
	into STRICT	ds_retorno_w
	from	pls_grau_participacao
	where	cd_tiss			= ie_funcao_executor_imp_w
	and	((ie_situacao = 'A') or (coalesce(ie_situacao::text, '') = ''));
elsif (ie_tipo_retorno_p	= 'CBO') then
	select	max(nr_sequencia)
	into STRICT	ds_retorno_w
	from	cbo_saude
	where	cd_cbo	= cd_cbo_saude_exec_imp_w
	and	((ie_situacao = 'A') or (coalesce(ie_situacao::text, '') = ''));
elsif (ie_tipo_retorno_p	= 'CBOSOLIC') then
	select	max(nr_sequencia)
	into STRICT	ds_retorno_w
	from	cbo_saude
	where	cd_cbo	= cd_cbo_saude_solic_imp_w
	and	((ie_situacao = 'A') or (coalesce(ie_situacao::text, '') = ''));
elsif (ie_tipo_retorno_p = 'MS')then
	select	coalesce(max(a.cd_pessoa_fisica),0)
	into STRICT	ds_retorno_w
	from	medico 			a,
		pessoa_fisica 		b,
		conselho_profissional	c
	where	a.cd_pessoa_fisica	= b.cd_pessoa_fisica
	and	b.nr_seq_conselho	= c.nr_sequencia
	and	a.nr_crm		= nr_crm_solic_imp_w
	and	a.uf_crm		= uf_crm_solic_imp_w
	and	c.sg_conselho		= sg_conselho_solic_imp_w;

	if ( ds_retorno_w = 0 ) then
		ds_retorno_w := null;
	end if;
end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
-- REVOKE ALL ON FUNCTION pls_obter_dados_gerar_conta ( nr_seq_conta_p integer, ie_tipo_retorno_p text) FROM PUBLIC;

