SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION pls_obter_dados_grupo_ajuste ( nr_seq_grupo_ajuste_p bigint, ie_tipo_p text) RETURNS varchar AS $body$
DECLARE


ds_retorno_w		varchar(255);
vl_dominio_w		smallint;

/*ie_tipo_p
T - Tipo do grupo de ajuste */
BEGIN

select	ie_tipo
into STRICT	vl_dominio_w
from	pls_grupo_ajuste_mat
where	nr_sequencia = nr_seq_grupo_ajuste_p;

if (ie_tipo_p = 'T') and (vl_dominio_w <> 0) then
	begin
	select	a.ds_valor_dominio
	into STRICT	ds_retorno_w
	from	dominio b,
		valor_dominio a
	where 	a.cd_dominio = b.cd_dominio
	and	a.cd_dominio = 4461
	and	a.vl_dominio = vl_dominio_w;
	end;
end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
-- REVOKE ALL ON FUNCTION pls_obter_dados_grupo_ajuste ( nr_seq_grupo_ajuste_p bigint, ie_tipo_p text) FROM PUBLIC;

