CREATE OR REPLACE FUNCTION pls_obter_dados_grupo_ans ( nr_seq_grupo_ans_p bigint, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE


/*	ie_opcao_p
	'D' - Descrição grupo
	'T' - Tipo de despesa
*/
ds_retorno_w			varchar(255);
ds_grupo_w			ans_grupo_despesa.ds_grupo%type;
ie_tipo_grupo_w			varchar(1);


BEGIN

begin
select	ds_grupo,
	ie_tipo_grupo
into STRICT	ds_grupo_w,
	ie_tipo_grupo_w
from	ans_grupo_despesa
where	nr_sequencia	= nr_seq_grupo_ans_p;
exception
when others then
	ds_grupo_w	:= '';
	ie_tipo_grupo_w	:= '';
end;

if (ie_opcao_p	= 'D') then
	ds_retorno_w	:= ds_grupo_w;
elsif (ie_opcao_p 	= 'T') then
	ds_retorno_w	:= ie_tipo_grupo_w;
end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
