CREATE OR REPLACE FUNCTION pls_obter_dados_grupo_produto ( nr_seq_grupo_p bigint, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE


/*++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++
Finalidade: Obter os dados dos grupos de produto
-------------------------------------------------------------------------------------------------------------------
Locais de chamada direta:
[X]  Objetos do dicionário [  ] Tasy (Delphi/Java) [  ] Portal [  ]  Relatórios [ ] Outros:
 ------------------------------------------------------------------------------------------------------------------
Pontos de atenção:
++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++*/ds_retorno_w		varchar(255);


BEGIN
if (nr_seq_grupo_p IS NOT NULL AND nr_seq_grupo_p::text <> '') then
	if (ie_opcao_p = 'DS') then
		select	ds_grupo
		into STRICT	ds_retorno_w
		from	pls_preco_grupo_produto
		where	nr_sequencia	= nr_seq_grupo_p;
	end if;
end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
