CREATE OR REPLACE FUNCTION pls_obter_dados_grupo_serv_ptu ( nr_seq_grupo_servico_p bigint, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE

/*ie_opcao_p
N - Descrição do grupo de serviço
*/
ds_retorno_w		varchar(255);

BEGIN
if (coalesce(nr_seq_grupo_servico_p,0) > 0) then
	if (ie_opcao_p = 'N') then
		select	max(ds_grupo_servico)
		into STRICT	ds_retorno_w
		from	ptu_grupo_servico
		where	cd_grupo_servico = nr_seq_grupo_servico_p;
	end if;

end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
