CREATE OR REPLACE FUNCTION pls_obter_dados_grupo_serv ( nr_seq_grupo_serv_p bigint, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE


ds_retorno_w		varchar(255)	:= null;


BEGIN
if (ie_opcao_p = 'D') then
	select	max(a.ds_grupo)
	into STRICT	ds_retorno_w
	from	pls_preco_grupo_servico	a
	where	a.nr_sequencia	= nr_seq_grupo_serv_p;
elsif (ie_opcao_p = 'DR') then
	select	to_char(max(a.dt_referencia),'dd/mm/yyyy')
	into STRICT	ds_retorno_w
	from	pls_preco_grupo_servico	a
	where	a.nr_sequencia	= nr_seq_grupo_serv_p;
else
	ds_retorno_w	:= 'N/A';
end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
