CREATE OR REPLACE FUNCTION pls_obter_dados_guia_fat ( nr_seq_lote_p pls_lote_faturamento.NR_SEQUENCIA%type, nr_seq_fat_p pls_fatura.nr_sequencia%type, cd_guia_ref_p pls_conta.cd_guia_referencia%type, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE


/*
_*_*_*_*_*_*_*_*_*_IE_OPCAO_P_*_*_*_*_*_*_*_*_*_
	* F	= Seq fatura
	* FE	= Seq Fat Evento
*/
ds_retorno_w	varchar(255);
ie_opcao_w	varchar(255);


BEGIN

select	upper(ie_opcao_p)
into STRICT	ie_opcao_w
;

if (ie_opcao_w = 'F') then
	select	max(b.nr_sequencia)
	into STRICT	ds_retorno_w
	from	pls_fatura		b,
		pls_fatura_evento	c,
		pls_fatura_conta	d
	where	c.nr_seq_fatura		= b.nr_sequencia
	and	d.nr_seq_fatura_evento	= c.nr_sequencia
	and	b.nr_seq_lote		= nr_seq_lote_p
	and	d.cd_guia_referencia	= cd_guia_ref_p;

elsif (ie_opcao_w = 'FE') then
	select	max(c.nr_sequencia)
	into STRICT	ds_retorno_w
	from	pls_fatura		b,
		pls_fatura_evento	c,
		pls_fatura_conta	d
	where	c.nr_seq_fatura		= b.nr_sequencia
	and	d.nr_seq_fatura_evento	= c.nr_sequencia
	and	b.nr_seq_lote		= nr_seq_lote_p
	and	b.nr_sequencia		= nr_seq_fat_p
	and	d.cd_guia_referencia	= cd_guia_ref_p;

end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
