CREATE OR REPLACE FUNCTION pls_obter_dados_guia_intern ( nr_seq_guia_p bigint, nr_seq_clinica_p bigint, ie_tipo_consulta_p text) RETURNS bigint AS $body$
DECLARE

/*
ie_tipo_consulta_p
QA	- Quantidades de dias autorizados
QR	- Quantidade real de dias que o beneficiário ficou internado DT_ALTA menos DT_ENTRADA
QS	- Quantidades de guias
PR	- Quantidades de prorrogações da guia
VT	- Valor total liberado na Conta
QPR	- Quantidade de Dias autorizados nas prorrogações
QU	- Quantidade de UTI
*/
cd_medico_solicitante_w			varchar(10);
cd_procedimento_w			bigint;

ie_origem_proced_w			bigint;
ie_classificacao_w			varchar(4);

nr_seq_segurado_w			bigint;
nr_seq_prestador_w			bigint;
nr_seq_guia_proc_w			bigint;

qt_retorno_w				double precision;
qt_dia_solicitado_w			bigint;
qt_autorizado_w				bigint	:= 0;
qt_autorizada_w				bigint;

C01 CURSOR FOR
	SELECT	nr_sequencia,
		cd_procedimento,
		ie_origem_proced,
		coalesce(qt_autorizada,0)
	from	pls_guia_plano_proc
	where	nr_seq_guia	= nr_seq_guia_p
	and	ie_status	= 'S';


BEGIN

begin
select	nr_seq_segurado,
	cd_medico_solicitante,
	coalesce(qt_dia_solicitado,0),
	nr_seq_prestador
into STRICT	nr_seq_segurado_w,
	cd_medico_solicitante_w,
	qt_dia_solicitado_w,
	nr_seq_prestador_w
from	pls_guia_plano
where	nr_sequencia	= nr_seq_guia_p
and	ie_estagio	in (3,6,5,10);
exception
when others then
	nr_seq_segurado_w		:= null;
	cd_medico_solicitante_w		:= null;
	qt_dia_solicitado_w		:= 0;
end;

if (ie_tipo_consulta_p	= 'QA') then
	open C01;
	loop
	fetch C01 into
		nr_seq_guia_proc_w,
		cd_procedimento_w,
		ie_origem_proced_w,
		qt_autorizada_w;
	EXIT WHEN NOT FOUND; /* apply on C01 */
		begin
		select	ie_classificacao
		into STRICT	ie_classificacao_w
		from	procedimento
		where	cd_procedimento		= cd_procedimento_w
		and	ie_origem_proced	= ie_origem_proced_w;

		if (ie_classificacao_w	= '3') then
			qt_autorizado_w	:= qt_autorizado_w + qt_autorizada_w;
		end if;
		end;
	end loop;
	close C01;

	if (coalesce(qt_autorizado_w,0)	= 0) then
		qt_retorno_w	:= coalesce(qt_dia_solicitado_w,0);
	else
		qt_retorno_w	:= qt_autorizado_w;
	end if;
elsif (ie_tipo_consulta_p	= 'QR') then
	begin

	select	sum(trunc(coalesce(b.dt_alta,clock_timestamp())) - trunc(coalesce(a.dt_admissao_hosp,b.dt_internacao)))
	into STRICT	qt_retorno_w
	from	pls_guia_atendimento	b,
		pls_guia_plano		a
	where	b.nr_seq_guia	= a.nr_sequencia
	and	b.ie_tipo_registro = 1
	and	a.ie_estagio	in (3,6,5,10)
	and (a.nr_sequencia = nr_seq_guia_p
	or	a.nr_seq_guia_principal = nr_seq_guia_p);
	exception
	when others then
		qt_retorno_w	:= 0;
	end;
	/*
	select	trunc(nvl(a.dt_alta,sysdate)) - trunc(nvl(b.dt_admissao_hosp,a.dt_entrada))
	into	qt_retorno_w
	from	pls_conta	a,
		pls_guia_plano	b
	where	b.nr_sequencia		= nr_seq_guia_p
	and	a.cd_guia		= b.cd_guia
	and	a.nr_seq_segurado	= b.nr_seq_segurado
	and	b.ie_estagio		in (3,6);
	exception
	when others then
		qt_retorno_w	:= 0;
	end;*/
	if (qt_retorno_w = 0) then
		begin
		select 	count(1)
		into STRICT	qt_retorno_w
		from	pls_guia_atendimento	b,
			pls_guia_plano		a
		where	b.nr_seq_guia	= a.nr_sequencia
		and	b.ie_tipo_registro = 1
		and	a.ie_estagio	in (3,6,5,10)
		and (a.nr_sequencia = nr_seq_guia_p
		or	a.nr_seq_guia_principal = nr_seq_guia_p);
		exception
		when others then
			qt_retorno_w	:= 0;
		end;

		if (qt_retorno_w > 0) then
			qt_retorno_w := 1;
		end if;
	end if;
	if (coalesce(qt_retorno_w::text, '') = '') then
		qt_retorno_w	:= 0;
	end if;
elsif (ie_tipo_consulta_p	= 'QS') then
	qt_retorno_w	:= coalesce(qt_dia_solicitado_w,0);
elsif (ie_tipo_consulta_p	= 'PR') then
	select	sum(b.qt_autorizada)
	into STRICT	qt_retorno_w
	from	pls_guia_plano	a,
		pls_guia_plano_proc	b
	where	a.cd_guia_principal in (	SELECT	x.cd_guia
						from	pls_guia_plano x
						where	x.nr_sequencia	= nr_seq_guia_p)
	and	a.nr_sequencia	= b.nr_seq_guia
	and	a.ie_estagio	in (3,6);
elsif (ie_tipo_consulta_p	= 'VT') then
	begin
	select	vl_total
	into STRICT	qt_retorno_w
	from	pls_conta	a,
		pls_guia_plano	b
	where	b.nr_sequencia		= nr_seq_guia_p
	and	a.cd_guia		= b.cd_guia
	and	a.nr_seq_segurado	= b.nr_seq_segurado;
	exception
	when others then
		qt_retorno_w	:= 0;
	end;

	if (coalesce(qt_retorno_w::text, '') = '') then
		qt_retorno_w	:= 0;
	end if;
elsif (ie_tipo_consulta_p	= 'QPR') then
	begin
		select	sum(qt_dia_autorizado)
		into STRICT	qt_retorno_w
		from	pls_guia_plano
		where	nr_seq_guia_principal = nr_seq_guia_p
		and	nr_sequencia <> nr_seq_guia_p;
	exception
	when others then
		qt_retorno_w := 0;
	end;
	if (coalesce(qt_retorno_w::text, '') = '') then
		qt_retorno_w	:= 0;
	end if;
elsif (ie_tipo_consulta_p	= 'QU') then
	select	sum(qt_dias_uti)
	into STRICT	qt_retorno_w
	from	pls_guia_atendimento	b,
		pls_guia_plano		a
	where	b.nr_seq_guia	= a.nr_sequencia
	and	b.ie_tipo_registro = 1
	and	a.ie_estagio	in (3,6,5,10)
	and (a.nr_sequencia = nr_seq_guia_p
	or	a.nr_seq_guia_principal = nr_seq_guia_p);

	if (coalesce(qt_retorno_w::text, '') = '') then
		qt_retorno_w	:= 0;
	end if;
end if;

return	qt_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
-- REVOKE ALL ON FUNCTION pls_obter_dados_guia_intern ( nr_seq_guia_p bigint, nr_seq_clinica_p bigint, ie_tipo_consulta_p text) FROM PUBLIC;

