CREATE OR REPLACE FUNCTION pls_obter_dados_guia_monit ( nr_seq_guia_monit_ret_p pls_monitor_tiss_guia_ret.nr_sequencia%type, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE

/* +++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++
Finalidade: Obter as informações de guia do monitoramento ANS.
-------------------------------------------------------------------------------------------------------------------
Locais de chamada direta:
[  X]  Objetos do dicionário [ ] Tasy (Delphi/Java) [  ] Portal [  ]  Relatórios [ ] Outros:
 ------------------------------------------------------------------------------------------------------------------
Pontos de atenção:
++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++ */
ds_retorno_w			varchar(255);
nr_seq_conta_w			pls_conta.nr_sequencia%type;
nr_seq_guia_monitor_w		pls_monitor_tiss_guia.nr_sequencia%type;


BEGIN
if (ie_opcao_p = 'C') then
	select	nr_seq_guia_monitor
	into STRICT	nr_seq_guia_monitor_w
	from	pls_monitor_tiss_guia_ret
	where	nr_sequencia = nr_seq_guia_monit_ret_p;

	select	nr_seq_conta
	into STRICT	nr_seq_conta_w
	from	pls_monitor_tiss_guia
	where	nr_sequencia = nr_seq_guia_monitor_w;

	ds_retorno_w := nr_seq_conta_w;
end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
