SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION pls_obter_dados_guia_proc ( nr_seq_guia_proc_p bigint, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE


/* ie_opcao_p
	P - Cod procedimento
	I - Origem procedimento
	D - Descricao procedimento
	QA - Quantidade autorizada
	QS - Quantidade solicitada
*/
ds_retorno_w		varchar(255);
cd_procedimento_w	bigint;
ie_origem_proced_w	bigint;
ds_procedimento_w	varchar(255);
qt_solicitada_w		pls_guia_plano_proc.qt_solicitada%type;
qt_autorizada_w		pls_guia_plano_proc.qt_autorizada%type;


BEGIN

select	cd_procedimento,
	ie_origem_proced,
	substr(obter_descricao_procedimento(cd_procedimento,ie_origem_proced),1,255),
	qt_solicitada,
	qt_autorizada
into STRICT	cd_procedimento_w,
	ie_origem_proced_w,
	ds_procedimento_w,
	qt_solicitada_w,
	qt_autorizada_w
from	pls_guia_plano_proc
where	nr_sequencia	= nr_seq_guia_proc_p;

if (ie_opcao_p = 'P') then
	ds_retorno_w := to_char(cd_procedimento_w);
elsif (ie_opcao_p = 'I') then
	ds_retorno_w := to_char(ie_origem_proced_w);
elsif (ie_opcao_p = 'D') then
	ds_retorno_w := ds_procedimento_w;
elsif (ie_opcao_p = 'QA') then
	ds_retorno_w := to_char(qt_autorizada_w);
elsif (ie_opcao_p = 'QS') then
	ds_retorno_w := to_char(qt_solicitada_w);
end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
