SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION pls_obter_dados_guia ( cd_guia_p text, ie_opcao_p text, cd_estabelecimento_p bigint) RETURNS varchar AS $body$
DECLARE

 
/*ie_opcao_p = 
	 VS = dt_validade_senha 
	 CIDP - CID principal 
	 CIDS - CID secundário 
	 DTA - Data de autorização 
	 TG - Tipo da guia 
	 EC - Estágio complemento 
	 CB - Carteira beneficiario 
	 CBI - Carteira beneficiário intercâmbio 
	 NS - Sequencia Segurado 
	 PCMSO - PCMSO da guia 
	 SNH - Senha da guia 
*/
 
 
ds_retorno_w		varchar(255);
nr_seq_guia_w		bigint;

 

BEGIN 
 
if (cd_estabelecimento_p IS NOT NULL AND cd_estabelecimento_p::text <> '') then 
	select	max(nr_sequencia) 
	into STRICT	nr_seq_guia_w 
	from	pls_guia_plano 
	where	cd_guia			= cd_guia_p 
	and	cd_estabelecimento 	= cd_estabelecimento_p;
else 
	select	max(nr_sequencia) 
	into STRICT	nr_seq_guia_w 
	from	pls_guia_plano 
	where	cd_guia			= cd_guia_p;
end if;
 
if (nr_seq_guia_w IS NOT NULL AND nr_seq_guia_w::text <> '') then 
	if (ie_opcao_p	= 'VS') then 
		begin 
		select	to_char(dt_validade_senha,'dd/mm/yyyy') 
		into STRICT	ds_retorno_w 
		from	pls_guia_plano 
		where	nr_sequencia	= nr_seq_guia_w;
		exception 
		when others then 
			ds_retorno_w	:= '';
		end;
	elsif (ie_opcao_p	= 'CIDP') then 
		select	max(cd_doenca) 
		into STRICT	ds_retorno_w 
		from	pls_diagnostico 
		where	nr_seq_guia 		= nr_seq_guia_w 
		and	ie_classificacao	= 'P';
	elsif (ie_opcao_p	= 'CIDS') then 
		select	max(cd_doenca) 
		into STRICT	ds_retorno_w 
		from	pls_diagnostico 
		where	nr_seq_guia 		= nr_seq_guia_w 
		and	ie_classificacao	= 'S';
	elsif (ie_opcao_p	= 'DTA') then 
		begin 
		select	to_char(dt_autorizacao,'dd/mm/yyyy') 
		into STRICT	ds_retorno_w 
		from	pls_guia_plano 
		where	nr_sequencia	= nr_seq_guia_w;
		exception 
		when others then 
			ds_retorno_w	:= '';
		end;
	elsif (ie_opcao_p	= 'TG') then 
		select	max(ie_tipo_guia) 
		into STRICT	ds_retorno_w 
		from	pls_guia_plano 
		where	nr_sequencia = nr_seq_guia_w;
	elsif (ie_opcao_p	= 'EC') then 
		select	max(ie_estagio_complemento) 
		into STRICT	ds_retorno_w 
		from	pls_guia_plano 
		where	nr_sequencia = nr_seq_guia_w;
	elsif (ie_opcao_p	= 'CB') then 
		select	substr(pls_obter_dados_segurado(nr_seq_segurado,'C'),1,255) 
		into STRICT	ds_retorno_w 
		from	pls_guia_plano 
		where	nr_sequencia = nr_seq_guia_w;
	elsif (ie_opcao_p	= 'CBI') then 
		select	substr(pls_obter_dados_segurado(nr_seq_segurado,'CI'),1,255) 
		into STRICT	ds_retorno_w 
		from	pls_guia_plano 
		where	nr_sequencia = nr_seq_guia_w;
	elsif (ie_opcao_p	= 'NS') then 
		select	max(nr_seq_segurado) 
		into STRICT	ds_retorno_w 
		from	pls_guia_plano 
		where	nr_sequencia = nr_seq_guia_w;
	elsif (ie_opcao_p	= 'PCMSO') then 
		select	max(ie_pcmso) 
		into STRICT	ds_retorno_w 
		from	pls_guia_plano 
		where	nr_sequencia = nr_seq_guia_w;
	elsif (ie_opcao_p	= 'SNH') then 
		select	max(cd_senha) 
		into STRICT	ds_retorno_w 
		from	pls_guia_plano 
		where	nr_sequencia = nr_seq_guia_w;
	end if;
end if;
 
return	ds_retorno_w;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
-- REVOKE ALL ON FUNCTION pls_obter_dados_guia ( cd_guia_p text, ie_opcao_p text, cd_estabelecimento_p bigint) FROM PUBLIC;

