CREATE OR REPLACE FUNCTION pls_obter_dados_hist_lead ( nr_seq_solicitacao_p pls_solicitacao_comercial.nr_sequencia%type, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE


/*	ie_opcao_p
	UH - último histórico
	UTA - último tipo atividade
	UDS - útimo histórico
	USQ - última sequência
*/
dt_ultimo_historico_w		pls_solicitacao_historico.dt_historico%type;
ie_tipo_atividade_w		pls_solicitacao_historico.ie_tipo_atividade%type;
ds_historico_w			pls_solicitacao_historico.ds_historico%type;
nr_seq_historico_w		pls_solicitacao_historico.nr_sequencia%type;
ds_retorno_w			varchar(4000);


BEGIN

dt_ultimo_historico_w := null;
ie_tipo_atividade_w := null;

begin
	select	dt_historico,
		ie_tipo_atividade,
		ds_historico,
		nr_sequencia
	into STRICT	dt_ultimo_historico_w,
		ie_tipo_atividade_w,
		ds_historico_w,
		nr_seq_historico_w
	from (SELECT	a.dt_historico,
			a.ie_tipo_atividade,
			a.ds_historico,
			a.nr_sequencia
		from	pls_solicitacao_historico a,
			pls_solicitacao_comercial b
		where	a.nr_seq_solicitacao = b.nr_sequencia
		and	b.nr_sequencia = nr_seq_solicitacao_p
		order by a.dt_historico desc) alias0 LIMIT 1;
exception
when others then
	dt_ultimo_historico_w := null;
	ie_tipo_atividade_w := null;
end;

if (ie_opcao_p = 'UH') then
	ds_retorno_w	:= to_char(dt_ultimo_historico_w,'dd/mm/yyyy hh:mm:ss');
elsif (ie_opcao_p = 'UTA') then
	ds_retorno_w	:= substr(pls_obter_desc_tipo_ativ(ie_tipo_atividade_w),1,255);
elsif (ie_opcao_p = 'UDS') then
	ds_retorno_w	:= substr(ds_historico_w,1,4000);
elsif (ie_opcao_p = 'USQ') then
	ds_retorno_w	:= nr_seq_historico_w;
end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
