SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION pls_obter_dados_impug_proc ( nr_seq_processo_proc_p pls_processo_procedimento.nr_sequencia%type, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE


/*++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++
IE_OPCAO_P
	CD - Código do procedimento
	DS - Descrição do procedimento
	TP - Tipo do procedimento
	QT - Quantidade de procedimento
	VL - Valor do procedimento
	VLU - Valor unitário do procedimento
	VLC - Valor calculado
*/ds_retorno_w			varchar(255) := null;


BEGIN

if (ie_opcao_p = 'CD') then
	select	max(cd_procedimento)
	into STRICT	ds_retorno_w
	from	pls_processo_procedimento
	where	nr_sequencia	= nr_seq_processo_proc_p;

elsif (ie_opcao_p = 'DS') then

	select	max(b.ds_procedimento)
	into STRICT	ds_retorno_w
	from	procedimento b,
		pls_processo_procedimento a
	where	a.nr_sequencia		= nr_seq_processo_proc_p
	and	a.cd_procedimento	= b.cd_procedimento
	and	a.ie_origem_proced	= b.ie_origem_proced;


elsif (ie_opcao_p = 'TP') then

	select	max(ie_tipo_procedimento)
	into STRICT	ds_retorno_w
	from	pls_processo_procedimento
	where	nr_sequencia	= nr_seq_processo_proc_p;

elsif (ie_opcao_p = 'QT') then

	select	max(qt_procedimento)
	into STRICT	ds_retorno_w
	from	pls_processo_procedimento
	where	nr_sequencia	= nr_seq_processo_proc_p;

elsif (ie_opcao_p = 'VL') then

	select	max(vl_procedimento)
	into STRICT	ds_retorno_w
	from	pls_processo_procedimento
	where	nr_sequencia	= nr_seq_processo_proc_p;

elsif (ie_opcao_p = 'VLU') then
	select	max(vl_unitario)
	into STRICT	ds_retorno_w
	from	pls_processo_procedimento
	where	nr_sequencia	= nr_seq_processo_proc_p;

elsif (ie_opcao_p = 'VLC') then
	select	max(vl_proc_calculado)
	into STRICT	ds_retorno_w
	from	pls_processo_procedimento
	where	nr_sequencia	= nr_seq_processo_proc_p;
end if;

return ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
