CREATE OR REPLACE FUNCTION pls_obter_dados_inconsist (nr_seq_inconsistencia_p bigint, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE


/*
ie_opcao_p
C - Código
D - Descrição
A - Ação do usuário
*/
ds_retorno_w	varchar(4000);


BEGIN

if (nr_seq_inconsistencia_p IS NOT NULL AND nr_seq_inconsistencia_p::text <> '') then
	if (ie_opcao_p	= 'C') then
		select	cd_inconsistencia
		into STRICT	ds_retorno_w
		from	pls_cad_inconsist_pessoa
		where	nr_sequencia	= nr_seq_inconsistencia_p;
	elsif (ie_opcao_p	= 'D') then
		select	ds_inconsistencia
		into STRICT	ds_retorno_w
		from	pls_cad_inconsist_pessoa
		where	nr_sequencia	= nr_seq_inconsistencia_p;
	elsif (ie_opcao_p	= 'A') then
		select	ds_acao_usuario
		into STRICT	ds_retorno_w
		from	pls_cad_inconsist_pessoa
		where	nr_sequencia	= nr_seq_inconsistencia_p;
	end if;
end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
