CREATE OR REPLACE FUNCTION pls_obter_dados_insconsitencia ( nr_sequencia_p bigint, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE


/*
Opções
D - Descrição
c - Código
*/
cd_motivo_w		bigint;
ds_motivo_w		varchar(255);


BEGIN

select	cd_inconsistencia,
	ds_inconsistencia
into STRICT	cd_motivo_w,
	ds_motivo_w
from	ptu_inconsistencia
where	nr_sequencia = nr_sequencia_p;

if (ie_opcao_p = 'C') then
	return	cd_motivo_w;
elsif (ie_opcao_p = 'D') then
	return	ds_motivo_w;
end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
