SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION pls_obter_dados_intercambio ( nr_seq_intercambio_p pls_intercambio.nr_sequencia%type, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE


/*
ie_opcao_p
	'N'	= Nome do estipulante
	'TE'	= Tipo de pessoa do contrato intercambio
	'TP'	= Tipo do contrato de intecambio
	'OE'	= Operadora Empresa
	'PA'	= Pagador
	'C'	= Codigo estipulante
	'TC'	= TC - Tipo de contratacao do produto do contrato
*/
ds_retorno_w		varchar(400);
cd_cgc_w		pessoa_juridica.cd_cgc%type;
cd_pf_w			pessoa_fisica.cd_pessoa_fisica%type;


BEGIN

-- Nome do estipulante
if (ie_opcao_p = 'N') then
	select	substr(obter_nome_pf_pj(cd_pessoa_fisica,cd_cgc),1,255)
	into STRICT	ds_retorno_w
	from	pls_intercambio
	where	nr_sequencia	= nr_seq_intercambio_p;
-- Tipo de pessoa do contrato de Intercambio
elsif (ie_opcao_p = 'TE') then
	select	cd_cgc
	into STRICT	cd_cgc_w
	from	pls_intercambio
	where	nr_sequencia	= nr_seq_intercambio_p;
	
	if (coalesce(cd_cgc_w::text, '') = '') then
		ds_retorno_w := 'PF';
	elsif (cd_cgc_w IS NOT NULL AND cd_cgc_w::text <> '') then
		ds_retorno_w := 'PJ';
	end if;
-- Tipo do contrato de intercambio
elsif (ie_opcao_p = 'TP') then
	select	max(ie_tipo_contrato)
	into STRICT	ds_retorno_w
	from	pls_intercambio
	where	nr_sequencia = nr_seq_intercambio_p;
-- Operadora Empresa
elsif (ie_opcao_p = 'OE') then
	select 	cd_operadora_empresa
	into STRICT	ds_retorno_w
	from 	pls_intercambio
	where 	nr_sequencia 	= nr_seq_intercambio_p;
-- Pagador
elsif (ie_opcao_p = 'PA') then
	select  max(b.nr_sequencia)
	into STRICT	ds_retorno_w
	from 	pls_contrato_pagador b	
	where	b.nr_seq_pagador_intercambio = nr_seq_intercambio_p
	and 	b.ie_tipo_pagador = 'P';
--Codigo estipulante
elsif (ie_opcao_p = 'C') then
	select	max(coalesce(cd_cgc, cd_pessoa_fisica))
	into STRICT	ds_retorno_w
	from	pls_intercambio
	where	nr_sequencia = nr_seq_intercambio_p;
--TC - Tipo de contratacao do produto do contrato	
elsif (ie_opcao_p = 'TC') then
	select	max(b.ie_tipo_contratacao)
	into STRICT	ds_retorno_w
	from	pls_intercambio_plano	a,
		pls_plano		b
	where	a.nr_seq_plano = b.nr_sequencia
	and	a.nr_seq_intercambio = nr_seq_intercambio_p
	and	a.ie_situacao = 'A';
	
	if (coalesce(ds_retorno_w::text, '') = '') then
		select	max(b.ie_tipo_contratacao)
		into STRICT	ds_retorno_w
		from	pls_intercambio_plano	a,
			pls_plano		b
		where	a.nr_seq_plano		= b.nr_sequencia
		and	a.nr_seq_intercambio	= nr_seq_intercambio_p;
	end if;
	
end if;	

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
-- REVOKE ALL ON FUNCTION pls_obter_dados_intercambio ( nr_seq_intercambio_p pls_intercambio.nr_sequencia%type, ie_opcao_p text) FROM PUBLIC;

