CREATE OR REPLACE FUNCTION pls_obter_dados_item_mens (nr_seq_item_p bigint, ie_tipo_p text) RETURNS varchar AS $body$
DECLARE


ds_retorno_w			varchar(255);
vl_item_w			pls_mensalidade_seg_item.vl_item%type;
ie_tipo_item_w			pls_mensalidade_seg_item.ie_tipo_item%type;
nr_seq_mensalidade_seg_w	pls_mensalidade_segurado.nr_sequencia%type;
nr_seq_mensalidade_w		pls_mensalidade.nr_sequencia%type;
nr_seq_lote_w			pls_lote_mensalidade.nr_sequencia%type;


BEGIN

select	a.vl_item,
	a.ie_tipo_item,
	a.nr_seq_mensalidade_seg,
	b.nr_seq_mensalidade,
	c.nr_seq_lote
into STRICT	vl_item_w,
	ie_tipo_item_w,
	nr_seq_mensalidade_seg_w,
	nr_seq_mensalidade_w,
	nr_seq_lote_w
from	pls_mensalidade_seg_item a,
	pls_mensalidade_segurado b,
	pls_mensalidade c
where	b.nr_sequencia	= a.nr_seq_mensalidade_seg
and	c.nr_sequencia	= b.nr_seq_mensalidade
and	a.nr_sequencia	= nr_seq_item_p;

if (ie_tipo_p	= 'V') then --Valor do item
	ds_retorno_w	:= vl_item_w;
elsif (ie_tipo_p	= 'D') then --Descrição do item
	ds_retorno_w	:= obter_valor_dominio(1930,ie_tipo_item_w);
elsif (ie_tipo_p	= 'MS') then --Sequência da mensalidade segurado
	ds_retorno_w	:= nr_seq_mensalidade_seg_w;
elsif (ie_tipo_p	= 'M') then --Sequência da mensalidade
	ds_retorno_w	:= nr_seq_mensalidade_w;
elsif (ie_tipo_p	= 'L') then --Lote da mensalidade
	ds_retorno_w	:= nr_seq_lote_w;
end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
