CREATE OR REPLACE FUNCTION pls_obter_dados_item_reemb ( nr_seq_reembolso_proc_p bigint, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE


ds_retorno_w			varchar(255);
cd_procedimento_w		bigint;
ds_procedimento_w		varchar(255);


BEGIN

if (nr_seq_reembolso_proc_p IS NOT NULL AND nr_seq_reembolso_proc_p::text <> '') then
	select	cd_procedimento,
		substr(obter_descricao_procedimento(cd_procedimento,ie_origem_proced),1,255)
	into STRICT	cd_procedimento_w,
		ds_procedimento_w
	from	pls_conta_proc
	where	nr_sequencia	= nr_seq_reembolso_proc_p;
end if;

if (ie_opcao_p = 'CP') then
	ds_retorno_w := to_char(cd_procedimento_w);
elsif (ie_opcao_p = 'DP') then
	ds_retorno_w := ds_procedimento_w;
end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
