CREATE OR REPLACE FUNCTION pls_obter_dados_lanc_prog ( nr_seq_lancamento_mens_p bigint, ie_tipo_valor_p text) RETURNS varchar AS $body$
DECLARE


ds_retorno_w		varchar(255);
ie_operacao_w		varchar(1);


BEGIN

if (ie_tipo_valor_p = 'VL') then
	select	CASE WHEN ie_operacao='D' THEN  vl_lancamento*-1  ELSE vl_lancamento END
	into STRICT	ds_retorno_w
	from (SELECT	max(a.vl_lancamento) vl_lancamento,
			max(b.ie_operacao_motivo) ie_operacao
		from	pls_lancamento_mensalidade a,
			pls_tipo_lanc_adic b
		where	b.nr_sequencia = a.nr_seq_motivo
		and	a.nr_sequencia = nr_seq_lancamento_mens_p) alias3;
	
	ds_retorno_w	:= campo_mascara_virgula(ds_retorno_w);
end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
