SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION pls_obter_dados_lib_guia ( nr_seq_guia_plano_p bigint, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE


ds_retorno_w			varchar(255)	:= null;
nm_usuario_liberacao_w		varchar(30)	:= null;
dt_liberacao_w			timestamp		:= null;
nr_seq_motivo_w			bigint	:= null;
ds_observacao_w			varchar(255)	:= null;
ds_motivo_liberacao_w		varchar(255)	:= null;
nr_seq_liberacao_w		bigint	:= null;
cd_estabelecimento_w		smallint	:= 0;


BEGIN
select	coalesce(max(nr_sequencia),0)
into STRICT	nr_seq_liberacao_w
from	pls_guia_motivo_lib
where	nr_seq_guia	= nr_seq_guia_plano_p;

select	cd_estabelecimento
into STRICT	cd_estabelecimento_w
from	pls_guia_plano
where	nr_sequencia = nr_seq_guia_plano_p;

if (nr_seq_liberacao_w	> 0) then
	select	b.nm_usuario_liberacao,
		b.dt_liberacao,
		b.nr_seq_motivo,
		substr(b.ds_observacao,1,255),
		c.ds_motivo_liberacao
	into STRICT	nm_usuario_liberacao_w,
		dt_liberacao_w,
		nr_seq_motivo_w,
		ds_observacao_w,
		ds_motivo_liberacao_w
	from	pls_guia_motivo_lib b,
		pls_motivo_liberacao c
	where	b.nr_seq_motivo	= c.nr_sequencia
	and	b.nr_sequencia	= nr_seq_liberacao_w
	and	c.cd_estabelecimento = cd_estabelecimento_w;
end if;

if (ie_opcao_p	= 'UL') then
	ds_retorno_w	:= nm_usuario_liberacao_w;
elsif (ie_opcao_p	= 'DL') then
	ds_retorno_w	:= to_char(dt_liberacao_w,'dd/mm/yyyy hh24:mi:ss');
elsif (ie_opcao_p	= 'NM') then
	ds_retorno_w	:= to_char(nr_seq_motivo_w);
elsif (ie_opcao_p	= 'OB') then
	ds_retorno_w	:= ds_observacao_w;
elsif (ie_opcao_p	= 'ML') then
	ds_retorno_w	:= ds_motivo_liberacao_w;
end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
