SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION pls_obter_dados_lote_notific ( nr_seq_lote_p bigint, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE


/* ie_opcao_p
	V - Valor lote
	VJ - Valor de juros
	VM - Valor de multa
	VP - Valor a pagar
	VT - Valot dos títulos
	VR - Valor rescisão
	QBR - Quantidade beneficiário rescindido
	QP - Quantidade pagadores
*/
ds_retorno_w			varchar(255);
vl_saldo_titulo_w		double precision;
vl_juros_w			double precision;
vl_multa_w			double precision;
vl_titulo_w			double precision;

vl_rescisao_w			double precision;
qt_rescisao_w			bigint;
qt_pagador_w			double precision;


BEGIN

select	count(*)
into STRICT	qt_pagador_w
from	pls_notificacao_pagador
where	nr_seq_lote	= nr_seq_lote_p;

select	sum(a.vl_saldo_titulo),
	sum(obter_juros_multa_titulo(a.nr_titulo,clock_timestamp(),'R','J')),
	sum(obter_juros_multa_titulo(a.nr_titulo,clock_timestamp(),'R','M')),
	sum(a.vl_titulo),
	sum(pls_benef_obter_rescindido(0, d.nr_seq_pagador, 2)),
	sum(pls_benef_obter_rescindido(0, d.nr_seq_pagador, 1))
into STRICT	vl_saldo_titulo_w,
	vl_juros_w,
	vl_multa_w,
	vl_titulo_w,
	vl_rescisao_w,
	qt_rescisao_w
from	titulo_receber a,
	pls_mensalidade b,
	pls_notificacao_item c,
	pls_notificacao_pagador d
where	b.nr_sequencia	= a.nr_seq_mensalidade
and	b.nr_sequencia	= c.nr_seq_mensalidade
and	d.nr_sequencia	= c.nr_seq_notific_pagador
and	d.nr_seq_lote	= nr_seq_lote_p;

if (ie_opcao_p 	= 'V') then
	ds_retorno_w	:= to_char(vl_saldo_titulo_w);
elsif (ie_opcao_p 	= 'VJ') then
	ds_retorno_w	:= to_char(vl_juros_w);
elsif (ie_opcao_p	= 'VM') then
	ds_retorno_w	:= to_char(vl_multa_w);
elsif (ie_opcao_p 	= 'VP') then
	ds_retorno_w	:= to_char(vl_saldo_titulo_w + vl_juros_w + vl_multa_w);
elsif (ie_opcao_p 	= 'VT') then
	ds_retorno_w	:= vl_titulo_w;
elsif (ie_opcao_p	= 'QBR') then
	ds_retorno_w	:= vl_rescisao_w;
elsif (ie_opcao_p	= 'VR') then
	ds_retorno_w	:= qt_rescisao_w;
elsif (ie_opcao_p	= 'QP') then
	ds_retorno_w	:= qt_pagador_w;
end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
-- REVOKE ALL ON FUNCTION pls_obter_dados_lote_notific ( nr_seq_lote_p bigint, ie_opcao_p text) FROM PUBLIC;

