CREATE OR REPLACE FUNCTION pls_obter_dados_lote_prot ( nr_seq_lote_p pls_lote_protocolo_conta.nr_sequencia%type, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE

/*	ie_opcao
		LA - Lote de apresentação automática do protocolo
*/
ds_retorno_w	varchar(500);

BEGIN
ds_retorno_w 	:= '';

if (ie_opcao_p = 'LA') then
	begin
		select	to_char(max(nr_seq_lote_apres_autom))
		into STRICT	ds_retorno_w
		from	pls_protocolo_conta
		where	nr_seq_lote_conta	= nr_seq_lote_p;
	exception
		when no_data_found then ds_retorno_w := '';
	end;
end if;

return ds_retorno_w;
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
