CREATE OR REPLACE FUNCTION pls_obter_dados_material_a900 ( cd_material_a900_p text, nr_seq_mat_unimed_p bigint, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE


ds_retorno_w		varchar(255);
cd_unidade_medida_w	varchar(10);
nm_material_w		varchar(255);
nr_seq_material_uni_w	bigint;
cd_material_uni_w	bigint;


BEGIN
/* Busca por sequência */

if (nr_seq_mat_unimed_p IS NOT NULL AND nr_seq_mat_unimed_p::text <> '') then
	select	max(cd_unidade_medida),
		max(nm_material),
		max(cd_material)
	into STRICT	cd_unidade_medida_w,
		nm_material_w,
		cd_material_uni_w
	from	pls_material_unimed
	where	nr_sequencia = nr_seq_mat_unimed_p;

/* Busca por código */

elsif (cd_material_a900_p IS NOT NULL AND cd_material_a900_p::text <> '') then
	select	max(cd_unidade_medida),
		max(nm_material),
		max(nr_sequencia)
	into STRICT	cd_unidade_medida_w,
		nm_material_w,
		nr_seq_material_uni_w
	from	pls_material_unimed
	where	cd_material = cd_material_a900_p;
end if;

if (upper(ie_opcao_p) = 'UM') then
	ds_retorno_w := cd_unidade_medida_w;
elsif (upper(ie_opcao_p) = 'NM') then
	ds_retorno_w := nm_material_w;
elsif (upper(ie_opcao_p) = 'NR') then
	ds_retorno_w := coalesce(nr_seq_material_uni_w,nr_seq_mat_unimed_p);
elsif (upper(ie_opcao_p) = 'CD') then
	ds_retorno_w := coalesce(cd_material_uni_w,cd_material_a900_p);
end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
