SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION pls_obter_dados_mat_guia_req (nr_seq_plano_mat_p bigint, nr_seq_req_mat_p bigint, tipo_informacao_p text) RETURNS varchar AS $body$
DECLARE


ds_material_w 		pls_material.ds_material%type;
cd_material_w		pls_material.cd_material%type;
retorno_w		varchar(255);

/*
* DS -> Descrição material
* CD -> Codigo material
*/
BEGIN

begin
	if (nr_seq_plano_mat_p IS NOT NULL AND nr_seq_plano_mat_p::text <> '') then
		select	substr(pls_obter_seq_codigo_material(nr_seq_material,null),1,255),
			substr(pls_obter_desc_material(nr_seq_material),1,255)
		into STRICT 	cd_material_w,
			ds_material_w
		from	pls_guia_plano_mat
		where	nr_sequencia = nr_seq_plano_mat_p;
	elsif (nr_seq_req_mat_p IS NOT NULL AND nr_seq_req_mat_p::text <> '') then
		select	substr(pls_obter_seq_codigo_material(nr_seq_material,null),1,255),
			substr(pls_obter_desc_material(nr_seq_material),1,255)
		into STRICT 	cd_material_w,
			ds_material_w
		from	pls_requisicao_mat
		where	nr_sequencia = nr_seq_req_mat_p;
	end if;
exception
when others then
	cd_material_w		:= null;
end;

if (tipo_informacao_p = 'DS') then
	retorno_w	:= ds_material_w;
elsif (tipo_informacao_p = 'CD') then
	retorno_w	:= cd_material_w;
end if;

return	retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
