CREATE OR REPLACE FUNCTION pls_obter_dados_medico_imp ( nr_conselho_prof_p text, cd_uf_conselho_prof_p text, cd_conselho_prof_p text, ie_campo_retorno_p text, ie_origem_p text) RETURNS varchar AS $body$
DECLARE


/* +++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++
Finalidade: Obter o código de pessoa física do médico enviado nas importações
-------------------------------------------------------------------------------------------------------------------
Locais de chamada direta:
[  ]  Objetos do dicionário [ ] Tasy (Delphi/Java) [  x] Portal [  ]  Relatórios [x ] Outros:
 ------------------------------------------------------------------------------------------------------------------
Pontos de atenção:
IE_ORIGEM_P
G - Guia

IE_CAMPO_RETORNO_P
C - Código pessoa física
++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++ */
cd_medico_w		pessoa_fisica.cd_pessoa_fisica%type := null;
ds_retorno_w		varchar(10);


BEGIN

--Origem Guia
if ( ie_origem_p = 'G' ) then
	select	max(somente_numero(b.cd_pessoa_fisica))
	into STRICT	cd_medico_w
	from	medico         a,
		pessoa_fisica  b
	where	a.cd_pessoa_fisica 	= b.cd_pessoa_fisica
	and	a.nr_crm		= nr_conselho_prof_p
	and	a.uf_crm		= cd_uf_conselho_prof_p
	and	b.nr_seq_conselho	= pls_obter_seq_conselho_tiss(cd_conselho_prof_p)
	and a.ie_situacao	= 'A';
end if;

-- Retorna código da pessoa física
if ( ie_campo_retorno_p = 'CP' ) then
	ds_retorno_w := cd_medico_w;
end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
