CREATE OR REPLACE FUNCTION pls_obter_dados_medico_prest ( cd_medico_p medico.cd_pessoa_fisica%type, cd_especialidade_medica_p medico_especialidade.cd_especialidade%type, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE


/*
_*_*_*_*_*_*_*_*_*_IE_OPCAO_P_*_*_*_*_*_*_*_*_*_
	* PG	= Pós - Graduação
*/
ds_retorno_w	varchar(255);
ie_opcao_w	varchar(255);


BEGIN

select	upper(ie_opcao_p)
into STRICT	ie_opcao_w
;

if (ie_opcao_w = 'PG') then
	select	max(e.ie_pos_grad_360)
	into STRICT	ds_retorno_w
	from	medico_especialidade	e
	where	e.cd_pessoa_fisica	= cd_medico_p
	and	((coalesce(cd_especialidade_medica_p::text, '') = '') or
		e.cd_especialidade	= cd_especialidade_medica_p);
end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
