CREATE OR REPLACE FUNCTION pls_obter_dados_medico (cd_pessoa_fisica_p text, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE


/* +++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++
Finalidade: Obter os dados dos medicos solicitantes/executores das autorizacoes.
-------------------------------------------------------------------------------------------------------------------

Locais de chamada direta: 
[  ]  Objetos do dicionario [ ] Tasy (Delphi/Java) [  ] Portal [  ]  Relatorios [ ] Outros:
 ------------------------------------------------------------------------------------------------------------------

Pontos de atencao: Performance.
++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++ */


/* 
ie_opcao_p
 NM - Nome do medico
 EC - Email do medico
 TC - Telefone Comercial
 CPF - Cpf do medico
 IN - Profissional inativo
*/
ds_retorno_w			varchar(255);


BEGIN

if (ie_opcao_p	= 'NM') then	-- Nome do medico
	begin
		select	nm_pessoa_fisica
		into STRICT	ds_retorno_w
		from	pessoa_fisica
		where	cd_pessoa_fisica	= cd_pessoa_fisica_p;
	exception
	when others then
		ds_retorno_w	:= '';
	end;
	
	
elsif (ie_opcao_p	= 'EC') then	-- Email comercial do medico
	begin
		select	ds_email
		into STRICT	ds_retorno_w
		from 	compl_pessoa_fisica
		where	cd_pessoa_fisica	= cd_pessoa_fisica_p
		and	ie_tipo_complemento 	= 2;
	exception
	when others then
		ds_retorno_w	:= '';
	end;
	
elsif (ie_opcao_p	= 'CPF') then	-- Cpf do medico
	begin
		select	nr_cpf
		into STRICT	ds_retorno_w
		from	pessoa_fisica
		where	cd_pessoa_fisica	= cd_pessoa_fisica_p;
	exception
	when others then
		ds_retorno_w	:= '';
	end;

elsif (ie_opcao_p	= 'TC') then	-- Telefone comercial
	begin
		select	nr_ddd_telefone ||''|| nr_telefone nr_telef_med_solic
		into STRICT	ds_retorno_w
		from 	compl_pessoa_fisica
		where	cd_pessoa_fisica	= cd_pessoa_fisica_p
		and	ie_tipo_complemento 	= 2;
	exception
	when others then
		ds_retorno_w	:= '';
	end;
	
elsif (ie_opcao_p	= 'IN') then 	-- Medico inativo
	begin
		select max(ie_situacao)
		into STRICT ds_retorno_w
		from medico
		where cd_pessoa_fisica	= cd_pessoa_fisica_p;
	exception
	when others then
		ds_retorno_w	:= '';
	end;	
end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
-- REVOKE ALL ON FUNCTION pls_obter_dados_medico (cd_pessoa_fisica_p text, ie_opcao_p text) FROM PUBLIC;

