CREATE OR REPLACE FUNCTION pls_obter_dados_mens_pendentes ( nr_seq_segurado_p bigint, dt_referencia_p timestamp, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE


cd_pessoa_fisica_w	varchar(10);
qt_meses_pendente_w	bigint;
ds_retorno_w		varchar(4000);

vl_mensalidade_w	double precision	:= 0;
dt_referencia_w		timestamp;
ds_referencia_w		varchar(4000)	:= '';
vl_mens_w		double precision;

qt_total_w		bigint;
qt_linha_w		bigint;
ds_barra_w		varchar(3)	:= '';

dt_rescisao_w		timestamp;

C01 CURSOR FOR
	SELECT	b.dt_referencia,
		sum(a.vl_mensalidade),
		row_number() OVER () AS rownum
	FROM pls_mensalidade_segurado a, pls_mensalidade b
LEFT OUTER JOIN titulo_receber c ON (b.nr_sequencia = c.nr_seq_mensalidade)
WHERE a.nr_seq_mensalidade	= b.nr_sequencia and coalesce(b.ie_cancelamento::text, '') = '' and a.nr_seq_segurado	= nr_seq_segurado_p and trunc(a.dt_mesano_referencia,'month')	<> dt_rescisao_w and ((c.ie_situacao		= '1') or (coalesce(c.ie_situacao::text, '') = '')) group by b.dt_referencia,
		rownum
	order by b.dt_referencia;


BEGIN

select	cd_pessoa_fisica,
	trunc(dt_rescisao,'month')
into STRICT	cd_pessoa_fisica_w,
	dt_rescisao_w
from	pls_segurado
where	nr_sequencia	= nr_seq_segurado_p;

/*qt_meses_pendente_w	:= pls_obter_qt_meses_pendentes(cd_pessoa_fisica_w,null);

if	(qt_meses_pendente_w > 1) then */
select	count(*)
into STRICT	qt_total_w
FROM pls_mensalidade_segurado a, pls_mensalidade b
LEFT OUTER JOIN titulo_receber c ON (b.nr_sequencia = c.nr_seq_mensalidade)
WHERE a.nr_seq_mensalidade	= b.nr_sequencia and coalesce(b.ie_cancelamento::text, '') = '' and a.nr_seq_segurado	= nr_seq_segurado_p and trunc(a.dt_mesano_referencia,'month')	<> dt_rescisao_w and ((c.ie_situacao		= '1') or (coalesce(c.ie_situacao::text, '') = ''));

if (qt_total_w > 0) then
	open C01;
	loop
	fetch C01 into
		dt_referencia_w,
		vl_mens_w,
		qt_linha_w;
	EXIT WHEN NOT FOUND; /* apply on C01 */
		begin
		vl_mensalidade_w	:= coalesce(vl_mensalidade_w,0) + coalesce(vl_mens_w,0);

		if (coalesce(ds_referencia_w::text, '') = '') then
			ds_barra_w	:= '';
		else
			ds_barra_w	:= '/';
		end if;

		--if	(to_char(dt_referencia_w,'mm') = '12') then
		--	ds_referencia_w		:= ds_referencia_w || ds_barra_w || substr(to_char(dt_referencia_w,'month'),1,3)||to_char(dt_referencia_w,'yy');
		--else
			ds_referencia_w		:= ds_referencia_w || ds_barra_w || substr(to_char(dt_referencia_w,'month'),1,3);
		--end if;
		if	((qt_total_w = qt_linha_w) or (to_char(dt_referencia_w,'mm') = '12')) then
			ds_referencia_w		:= ds_referencia_w || ds_barra_w || to_char(dt_referencia_w,'yy');
		end if;

		end;
	end loop;
	close C01;

	--ds_referencia_w	:= ds_referencia_w || '/' ||to_char(dt_rescisao_w,'yy');
	/*ds_referencia_w		:= ds_referencia_w || ds_barra_w || substr(to_char(dt_referencia_p,'month'),1,3)||'/'||to_char(dt_referencia_p,'yy');*/

else
	select	b.dt_referencia
	into STRICT	dt_referencia_w
	from	pls_mensalidade_segurado	a,
		pls_mensalidade			b
	where	a.nr_seq_mensalidade	= b.nr_sequencia
	and	coalesce(b.ie_cancelamento::text, '') = ''
	and	a.nr_seq_segurado	= nr_seq_segurado_p
	and	b.dt_referencia		= dt_referencia_p;

	ds_referencia_w		:= substr(to_char(dt_referencia_w),1,3)||to_char(dt_referencia_w,'yy');
end if;

if (ie_opcao_p = 'R') then
	ds_retorno_w	:= ds_referencia_w;
elsif (ie_opcao_p = 'V') then
	ds_retorno_w	:= (vl_mensalidade_w)::numeric;
end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
