CREATE OR REPLACE FUNCTION pls_obter_dados_meta_equipe ( nr_seq_vend_meta_p bigint, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE


ds_retorno_w	varchar(255);


BEGIN

if (ie_opcao_p = 'O') then
	select	substr(max(ds_observacao),1,255)
	into STRICT	ds_retorno_w
	from	pls_equipe_vend_meta
	where	nr_sequencia	= nr_seq_vend_meta_p;
end if;

return	ds_retorno_w;

end;
$body$
