CREATE OR REPLACE FUNCTION pls_obter_dados_motivo_via ( nr_seq_motivo_via_p bigint, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE

/* ie_opcao_p
	N - Nome - descrição motivo via adicional
	V - Válida - Retorna 'S' se a via foi invalidada e 'N' se a via é válida
	VA - Válidade descrição - Retorna 'válida' se a via for válida e 'inválida' se a via for inválida
*/
ds_retorno_w	varchar(255);
ds_motivo_w	varchar(255);


BEGIN


if (ie_opcao_p = 'N') then

	select	max(ds_motivo)
	into STRICT	ds_motivo_w
	from	pls_motivo_via_adicional
	where	nr_sequencia	= nr_seq_motivo_via_p;

	ds_retorno_w := ds_motivo_w;
elsif (ie_opcao_p = 'V') then

	select	coalesce(max(ie_inv_vias_anterior),'N')
	into STRICT	ds_retorno_w
	from	pls_motivo_via_adicional
	where	nr_sequencia	= nr_seq_motivo_via_p;

elsif (ie_opcao_p = 'VA') then
	select	CASE WHEN max(ie_inv_vias_anterior)='S' THEN  substr(obter_desc_expressao(796824),1,255)  ELSE substr(obter_desc_expressao(331743),1,255) END
	into STRICT	ds_retorno_w
	from	pls_motivo_via_adicional
	where	nr_sequencia	= nr_seq_motivo_via_p;

end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
