CREATE OR REPLACE FUNCTION pls_obter_dados_mot_pag_isenc ( nr_sequencia_p bigint, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE


ds_retorno_w		varchar(255) := null;


BEGIN
if (upper(ie_opcao_p) = upper('ds')) then
	select	substr(ds_motivo,1,255)
	into STRICT	ds_retorno_w
	from	pls_motivo_pag_isencao
	where	nr_sequencia	= nr_sequencia_p;
end if;

return	ds_retorno_w;

end;
$body$
