CREATE OR REPLACE FUNCTION pls_obter_dados_mot_saida_int ( nr_seq_mot_saida_p bigint, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE

/* ie_opcao_p
	D	- Descrição
	TI	- Código TISS
	PT	- Código PTU
	S	- Situação
	V	- Versão
	O	- Óbito */
ds_retorno_w		varchar(255);


BEGIN
if (ie_opcao_p = 'D') then
	select	max(a.ds_motivo_saida)
	into STRICT	ds_retorno_w
	from	pls_motivo_saida	a
	where	a.nr_sequencia		= nr_seq_mot_saida_p;
elsif (ie_opcao_p = 'TI') then
	select	max(a.cd_tiss)
	into STRICT	ds_retorno_w
	from	pls_motivo_saida	a
	where	a.nr_sequencia		= nr_seq_mot_saida_p;
elsif (ie_opcao_p = 'PT') then
	select	max(a.cd_ptu)
	into STRICT	ds_retorno_w
	from	pls_motivo_saida	a
	where	a.nr_sequencia		= nr_seq_mot_saida_p;
elsif (ie_opcao_p = 'S') then
	select	max(a.ie_situacao)
	into STRICT	ds_retorno_w
	from	pls_motivo_saida	a
	where	a.nr_sequencia		= nr_seq_mot_saida_p;
elsif (ie_opcao_p = 'V') then
	select	max(a.ie_versao)
	into STRICT	ds_retorno_w
	from	pls_motivo_saida	a
	where	a.nr_sequencia		= nr_seq_mot_saida_p;
elsif (ie_opcao_p = 'O') then
	select	max(a.ie_obito)
	into STRICT	ds_retorno_w
	from	pls_motivo_saida	a
	where	a.nr_sequencia		= nr_seq_mot_saida_p;
end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
