SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION pls_obter_dados_movto_sem_cta ( nr_seq_movimento_p bigint, ie_item_relatorio_p text, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE


/*	ie_item_relatorio_p************
	CM = Contas Médicas
	M = Mensalidade
	RV = Repasse Vendedor
	R = Reembolso

	ie_opcao_p******************
	C = Classificação contábil
*/
ds_retorno_w	varchar(255);

nr_seq_item_mens_w		bigint;
ie_tipo_ato_cooperado_w		varchar(2);
ie_conta_antecipacao_w		varchar(1);
ie_deb_cred_w			varchar(1);

ie_tipo_item_w			varchar(2);
nr_seq_conta_w			bigint;
cd_classif_cred_w		varchar(255);
cd_classif_deb_w		varchar(255);
cd_classif_cred_ant_w		varchar(255);
cd_classif_deb_ant_w		varchar(255);
cd_classif_cooperado_w		varchar(255);
cd_classif_ato_auxiliar_w	varchar(255);
cd_classif_nao_cooperado_w	varchar(255);


BEGIN

if (ie_item_relatorio_p = 'M') then
	begin
	select	cd_item,
		ie_tipo_ato_cooperado,
		ie_conta_antecipacao,
		ie_deb_cred
	into STRICT	nr_seq_item_mens_w,
		ie_tipo_ato_cooperado_w,
		ie_conta_antecipacao_w,
		ie_deb_cred_w
	from	w_pls_movimento_sem_conta
	where	nr_sequencia	= nr_seq_movimento_p;

	select	ie_tipo_item,
		nr_seq_conta,
		cd_classif_cred,
		cd_classif_deb,
		cd_classif_cred_ant,
		cd_classif_deb_ant,
		cd_classif_cooperado,
		cd_classif_ato_auxiliar,
		cd_classif_nao_cooperado
	into STRICT	ie_tipo_item_w,
		nr_seq_conta_w,
		cd_classif_cred_w,
		cd_classif_deb_w,
		cd_classif_cred_ant_w,
		cd_classif_deb_ant_w,
		cd_classif_cooperado_w,
		cd_classif_ato_auxiliar_w,
		cd_classif_nao_cooperado_w
	from	pls_mensalidade_seg_item
	where	nr_sequencia	= nr_seq_item_mens_w;
	exception
	when others then
		null;
	end;

	if (ie_tipo_item_w	<> '3') then
		if (ie_tipo_ato_cooperado_w = '1') then
			ds_retorno_w	:= cd_classif_cooperado_w;
		elsif (ie_tipo_ato_cooperado_w = '2') then
			ds_retorno_w	:= cd_classif_ato_auxiliar_w;
		elsif (ie_tipo_ato_cooperado_w = '3') then
			ds_retorno_w	:= cd_classif_nao_cooperado_w;
		elsif (ie_conta_antecipacao_w = 'N') then
			if (ie_deb_cred_w = 'D') then
				ds_retorno_w	:= cd_classif_deb_w;
			elsif (ie_deb_cred_w = 'C') then
				ds_retorno_w	:= cd_classif_cred_w;
			end if;
		elsif (ie_conta_antecipacao_w = 'S') then
			if (ie_deb_cred_w = 'D') then
				ds_retorno_w	:= cd_classif_deb_ant_w;
			elsif (ie_deb_cred_w = 'C') then
				ds_retorno_w	:= cd_classif_cred_ant_w;
			end if;
		end if;
	elsif (ie_tipo_item_w = '3') then
		select	max(b.cd_classif_cred),
			max(b.cd_classif_deb)
		into STRICT	cd_classif_cred_w,
			cd_classif_deb_w
		from	pls_conta a,
			pls_conta_coparticipacao b
		where	b.nr_seq_conta	= a.nr_sequencia
		and	a.nr_sequencia	= nr_seq_conta_w;

		if (ie_deb_cred_w = 'D') then
			ds_retorno_w	:= cd_classif_deb_w;
		elsif (ie_deb_cred_w = 'C') then
			ds_retorno_w	:= cd_classif_cred_w;
		end if;
	end if;
end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
-- REVOKE ALL ON FUNCTION pls_obter_dados_movto_sem_cta ( nr_seq_movimento_p bigint, ie_item_relatorio_p text, ie_opcao_p text) FROM PUBLIC;

