CREATE OR REPLACE FUNCTION pls_obter_dados_mov_mens_benef ( nr_seq_mov_benef_p pls_mov_mens_benef.nr_sequencia%type, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE


ds_retorno_w	varchar(255);


BEGIN

if (ie_opcao_p = 'V') then --Valor total do benefici√°rio
	select	coalesce(sum(vl_item),0)
	into STRICT	ds_retorno_w
	from	pls_mov_mens_benef_item
	where	nr_seq_mov_benef	= nr_seq_mov_benef_p;
end if;

return	ds_retorno_w;

end;
