CREATE OR REPLACE FUNCTION pls_obter_dados_msg_glosa ( nr_seq_glosa_p bigint, ie_tipo_p text) RETURNS varchar AS $body$
DECLARE


/* ie_tipo_p
  'ME' - Mensagem externa
*/
ds_retorno_w			varchar(255);
ds_mensagem_externa_w		varchar(255);


BEGIN

select	max(ds_mensagem_externa)
into STRICT	ds_mensagem_externa_w
from	pls_msg_externa_glosa
where	ie_situacao = 'A'
and	nr_seq_motivo_glosa = nr_seq_glosa_p;

if (ie_tipo_p = 'ME') then
	ds_retorno_w := ds_mensagem_externa_w;
end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
