CREATE OR REPLACE FUNCTION pls_obter_dados_ndc_conclusao ( nr_seq_nota_deb_concl_p bigint, ie_opcao_p text ) RETURNS varchar AS $body$
DECLARE


/*++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++
Finalidade: Obter informações referente ao A560 (Dados da Nota de Débito da Conclusão)
-------------------------------------------------------------------------------------------------------------------
Locais de chamada direta:
[  ]  Objetos do dicionário [  ] Tasy (Delphi/Java) [  ] Portal [  ]  Relatórios [ ] Outros:
 ------------------------------------------------------------------------------------------------------------------
Pontos de atenção:		R562
++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++*/ds_retorno_w			varchar(4000) := null;


BEGIN
if (ie_opcao_p = 'TP') then
	select	max(nr_titulo)
	into STRICT	ds_retorno_w
	from	titulo_pagar
	where	nr_seq_nota_deb_conclusao = nr_seq_nota_deb_concl_p;

elsif (ie_opcao_p = 'TR') then
	select	max(nr_titulo)
	into STRICT	ds_retorno_w
	from	titulo_receber
	where	nr_seq_nota_deb_conclusao = nr_seq_nota_deb_concl_p;

elsif (ie_opcao_p = 'VP') then
	select	max(vl_titulo)
	into STRICT	ds_retorno_w
	from	titulo_pagar
	where	nr_seq_nota_deb_conclusao = nr_seq_nota_deb_concl_p;

elsif (ie_opcao_p = 'VR') then
	select	max(vl_titulo)
	into STRICT	ds_retorno_w
	from	titulo_receber
	where	nr_seq_nota_deb_conclusao = nr_seq_nota_deb_concl_p;

elsif (ie_opcao_p = 'DVP') then
	select	max(dt_vencimento_atual)
	into STRICT	ds_retorno_w
	from	titulo_pagar
	where	nr_seq_nota_deb_conclusao = nr_seq_nota_deb_concl_p;

elsif (ie_opcao_p = 'DVR') then
	select	max(dt_pagamento_previsto)
	into STRICT	ds_retorno_w
	from	titulo_receber
	where	nr_seq_nota_deb_conclusao = nr_seq_nota_deb_concl_p;

end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
