CREATE OR REPLACE FUNCTION pls_obter_dados_neg_mens_item ( nr_seq_neg_mens_item_p bigint, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE


/*
	ie_opcao_p
	- DSIM	- Descrição do item da mensalidade
*/
ds_retorno_w		varchar(255);


BEGIN

if (ie_opcao_p	= 'DSIM') then
	select	substr(ds_valor_dominio,1,60)
	into STRICT	ds_retorno_w
	from	valor_dominio
	where	cd_dominio 	= 1930
	and	vl_dominio	= (	SELECT	ie_tipo_item
					from	PLS_NEGOCIACAO_MENS_ITEM
					where	nr_sequencia	= nr_seq_neg_mens_item_p);
end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
