SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION pls_obter_dados_nota_a560 ( nr_seq_nota_debito_p bigint, ie_opcao_p text ) RETURNS varchar AS $body$
DECLARE


/*++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++
Finalidade: Obter informações referente ao A560 (Carga para Nota de Débito/Crédito)
-------------------------------------------------------------------------------------------------------------------
Locais de chamada direta:
[  ]  Objetos do dicionário [  ] Tasy (Delphi/Java) [  ] Portal [  ]  Relatórios [ ] Outros:
 ------------------------------------------------------------------------------------------------------------------
Pontos de atenção:
++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++*/ds_retorno_w			varchar(4000) := null;
nr_seq_camara_contest_w		bigint;
nr_seq_lote_contest_w		bigint;
nr_titulo_w			bigint;


BEGIN
if (ie_opcao_p = 'TP') then
	select	max(nr_titulo)
	into STRICT	ds_retorno_w
	from	titulo_pagar
	where	nr_seq_nota_debito = nr_seq_nota_debito_p;

elsif (ie_opcao_p = 'TR') then
	select	max(nr_nota_debito),
		max(nr_seq_camara_contest)
	into STRICT	nr_titulo_w,
		nr_seq_camara_contest_w
	from	ptu_nota_debito
	where	nr_sequencia = nr_seq_nota_debito_p;

	select	max(nr_seq_lote_contest)
	into STRICT	nr_seq_lote_contest_w
	from	ptu_camara_contestacao
	where	nr_sequencia = nr_seq_camara_contest_w;

	begin
	select	nr_titulo_receber
	into STRICT	ds_retorno_w
	from	pls_lote_discussao
	where	nr_seq_lote_contest	= nr_seq_lote_contest_w
	and	nr_titulo_receber	= nr_titulo_w;
	exception
	when others then
		ds_retorno_w := null;
	end;

elsif (ie_opcao_p = 'VP') then
	select	max(vl_titulo)
	into STRICT	ds_retorno_w
	from	titulo_pagar
	where	nr_seq_nota_debito = nr_seq_nota_debito_p;

end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
-- REVOKE ALL ON FUNCTION pls_obter_dados_nota_a560 ( nr_seq_nota_debito_p bigint, ie_opcao_p text ) FROM PUBLIC;

