CREATE OR REPLACE FUNCTION pls_obter_dados_notific_pag ( nr_seq_notific_pagador_p bigint, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE

 
/* ie_opcao_p 
	V - Valor pagador 
	VJ - Valor do juros 
	VM - Valor da multa 
	VP - Valor a pagar 
*/
 
 
ds_retorno_w			varchar(255);
vl_pagador_w			double precision;
vl_juros_w			double precision;
vl_multa_w			double precision;
vl_pagar_w			double precision;


BEGIN 
 
select	sum((substr(Obter_Dados_Titulo_Receber(pls_obter_titulo_mensalidade(nr_seq_mensalidade,null),'VS'),1,255))::numeric ), 
	sum((pls_obter_dados_item_notif(nr_sequencia,'VJ'))::numeric ), 
	sum((pls_obter_dados_item_notif(nr_sequencia,'VM'))::numeric ), 
	sum((pls_obter_dados_item_notif(nr_sequencia,'VP'))::numeric ) 
into STRICT	vl_pagador_w, 
	vl_juros_w, 
	vl_multa_w, 
	vl_pagar_w 
from	pls_notificacao_item 
where	nr_seq_notific_pagador	= nr_seq_notific_pagador_p;
 
if (ie_opcao_p	= 'V') then 
	ds_retorno_w	:= to_char(vl_pagador_w);
elsif (ie_opcao_p	= 'VJ') then 
	ds_retorno_w	:= to_char(vl_juros_w);
elsif (ie_opcao_p	= 'VM') then 
	ds_retorno_w	:= to_char(vl_multa_w);
elsif (ie_opcao_p	= 'VP') then 
	ds_retorno_w	:= to_char(vl_pagar_w);
end if;
 
return	ds_retorno_w;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
