CREATE OR REPLACE FUNCTION pls_obter_dados_notif_pagador ( nr_seq_notif_pagador_p pls_notificacao_pagador.nr_sequencia%type, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE


/*++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++
Finalidade: Retornar informações das notificações dos pagadores
-------------------------------------------------------------------------------------------------------------------
Locais de chamada direta:
[X]  Objetos do dicionário [  ] Tasy (Delphi/Java) [  ] Portal [  ]  Relatórios [ ] Outros:
 ------------------------------------------------------------------------------------------------------------------
Pontos de ATENÇÃO:

'CO'	= Cobrador atual
'ECO'	= Grupo de cobrança atual
'OC'	= Original cobrador

As opções acima são utilizadas exclusivamente na função OPS - Controle de Notificações de Atraso
++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++*/ds_retorno_w		varchar(4000);
nr_seq_cobrador_w	pls_notificacao_pagador.nr_seq_cobrador%type;
nr_seq_grupo_w		grupo_cobranca_membro.nr_seq_grupo%type;
nr_seq_cobranca_w	cobranca.nr_sequencia%type;
qt_grupo_w		integer;


BEGIN
if (nr_seq_notif_pagador_p IS NOT NULL AND nr_seq_notif_pagador_p::text <> '') then

	if (ie_opcao_p = 'CO') then
		select	max(a.nr_sequencia)
		into STRICT	nr_seq_cobranca_w
		from	cobranca a
		where	a.ie_status <> 'C'
		and	a.nr_titulo in (SELECT	x.nr_titulo
					from	pls_notificacao_item x
					where	x.nr_seq_notific_pagador = nr_seq_notif_pagador_p);

		if (nr_seq_cobranca_w IS NOT NULL AND nr_seq_cobranca_w::text <> '') then
			select	max(b.nm_pessoa_fisica)
			into STRICT	ds_retorno_w
			from	pessoa_fisica	b,
				cobrador	a,
				cobranca	c
			where	a.cd_pessoa_fisica	= b.cd_pessoa_fisica
			and	a.nr_sequencia		= c.nr_seq_cobrador
			and	c.nr_sequencia		= nr_seq_cobranca_w;
		end if;

	elsif (ie_opcao_p = 'ECO') then
		select	count(1)
		into STRICT	qt_grupo_w
		from	grupo_cobranca_membro LIMIT 1;

		if (qt_grupo_w > 0) then
			select	max(a.nr_sequencia)
			into STRICT	nr_seq_cobranca_w
			from	cobranca a
			where	a.ie_status <> 'C'
			and	a.nr_titulo in (SELECT	x.nr_titulo
						from	pls_notificacao_item x
						where	x.nr_seq_notific_pagador = nr_seq_notif_pagador_p);

			if (nr_seq_cobranca_w IS NOT NULL AND nr_seq_cobranca_w::text <> '') then
				select	max(b.nr_seq_grupo)
				into STRICT	nr_seq_grupo_w
				from	grupo_cobranca_membro	b,
					cobrador		a,
					cobranca		c
				where	a.nr_sequencia	= b.nr_seq_cobrador
				and	a.nr_sequencia	= c.nr_seq_cobrador
				and	c.nr_sequencia = nr_seq_cobranca_w;

				if (nr_seq_grupo_w IS NOT NULL AND nr_seq_grupo_w::text <> '') then
					select	a.ds_grupo
					into STRICT	ds_retorno_w
					from	grupo_cobranca	a
					where	a.nr_sequencia	= nr_seq_grupo_w;
				end if;
			end if;
		end if;

	elsif (ie_opcao_p = 'OC') then
		select	nr_seq_cobrador
		into STRICT	nr_seq_cobrador_w
		from	pls_notificacao_pagador	a
		where	a.nr_sequencia	= nr_seq_notif_pagador_p;

		if (nr_seq_cobrador_w IS NOT NULL AND nr_seq_cobrador_w::text <> '') then
			select	max(b.nm_pessoa_fisica)
			into STRICT	ds_retorno_w
			from	pessoa_fisica	b,
				cobrador	a
			where	a.cd_pessoa_fisica	= b.cd_pessoa_fisica
			and	a.nr_sequencia		= nr_seq_cobrador_w;
		end if;
	end if;

end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
-- REVOKE ALL ON FUNCTION pls_obter_dados_notif_pagador ( nr_seq_notif_pagador_p pls_notificacao_pagador.nr_sequencia%type, ie_opcao_p text) FROM PUBLIC;

