CREATE OR REPLACE FUNCTION pls_obter_dados_noti_reg_exce ( nr_sequencia_p pls_notific_regra_excecao.nr_sequencia%type, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE

/*	ie_opcao_p
	NM = Nome do Portador
*/
ds_retorno_w	varchar(255);

BEGIN
if (ie_opcao_p = 'NM') then
	begin
		select	p.ds_portador
		into STRICT	ds_retorno_w
		from	pls_notific_regra_excecao	r,
			portador			p
		where	p.cd_tipo_portador	= r.cd_tipo_portador
		and	p.cd_portador		= r.cd_portador
		and	r.nr_sequencia		= nr_sequencia_p;
	exception
		when no_data_found then ds_retorno_w := '';
	end;
end if;
return ds_retorno_w;
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
