CREATE OR REPLACE FUNCTION pls_obter_dados_nr_contrato ( nr_contrato_p pls_contrato.nr_contrato%type, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE


/*	ie_opcao_p
	E = Estipulante
	D - Data
	S - Descricao do contrato
	TC - Tipo de contratacao do produto do contrato
*/
ds_retorno_w		varchar(255);
ds_estipulante_w	varchar(255);
cd_pf_estipulante_w	varchar(10);
cd_cgc_estipulante_w	varchar(14);
dt_contrato_w		timestamp;
ds_contrato_w		varchar(255);


BEGIN

begin
	select	cd_pf_estipulante,
		cd_cgc_estipulante,
		dt_contrato,
		obter_nome_pf_pj(cd_pf_estipulante,cd_cgc_estipulante),
		ds_contrato
	into STRICT	cd_pf_estipulante_w,
		cd_cgc_estipulante_w,
		dt_contrato_w,
		ds_estipulante_w,
		ds_contrato_w
	from	pls_contrato
	where	nr_contrato	= nr_contrato_p;
exception
when others then
	cd_pf_estipulante_w	:= null;
	cd_cgc_estipulante_w	:= null;
	ds_contrato_w		:= null;
end;

if (ie_opcao_p = 'E') then
	ds_retorno_w	:= ds_estipulante_w;
elsif (ie_opcao_p = 'D') then
	ds_retorno_w	:= to_char(dt_contrato_w,'dd/mm/yyyy');
elsif (ie_opcao_p = 'S') then
	ds_retorno_w	:= ds_estipulante_w;
	if (ds_contrato_w IS NOT NULL AND ds_contrato_w::text <> '') then
		ds_retorno_w := ds_retorno_w ||' - '||ds_contrato_w;
	end if;
elsif (ie_opcao_p = 'TC') then
	select	max(c.ie_tipo_contratacao)
	into STRICT	ds_retorno_w
	from	pls_contrato_plano a,
		pls_contrato b,
		pls_plano c
	where	b.nr_sequencia	= a.nr_seq_contrato
	and	c.nr_sequencia	= a.nr_seq_plano
	and	b.nr_contrato	= nr_contrato_p
	and	a.ie_situacao	= 'A';
	
	if (coalesce(ds_retorno_w::text, '') = '') then
		select	max(c.ie_tipo_contratacao)
		into STRICT	ds_retorno_w
		from	pls_contrato_plano a,
			pls_contrato b,
			pls_plano c
		where	b.nr_sequencia	= a.nr_seq_contrato
		and	c.nr_sequencia	= a.nr_seq_plano
		and	b.nr_contrato	= nr_contrato_p;
	end if;
end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
