CREATE OR REPLACE FUNCTION pls_obter_dados_ocorrencia ( nr_seq_ocorrencia_p bigint, ie_tipo_p text) RETURNS varchar AS $body$
DECLARE


/* ie_tipo_p
  'C' - Código
  'D' - Descrição
  'ME' - Descrição externa
  'DE' - Somente Descrição externa - sem o nvl
  'DO' - Documentação
  'DCT' - Documentação da tabela PLS_OCOR_DOCUMENTACAO
*/
ds_retorno_w			varchar(255);
cd_ocorrencia_w			varchar(10);
ds_ocorrencia_w			varchar(255);
ds_mensagem_externa_w		varchar(255);
ds_mensagem_externa_ww		varchar(255);
ds_documentacao_w		varchar(255);
ds_ocor_document_w		varchar(4000);


BEGIN

select	cd_ocorrencia,
	ds_ocorrencia,
	coalesce(ds_mensagem_externa, ds_ocorrencia),
	ds_mensagem_externa,
	substr(ds_documentacao,1,255)
into STRICT	cd_ocorrencia_w,
	ds_ocorrencia_w,
	ds_mensagem_externa_w,
	ds_mensagem_externa_ww,
	ds_documentacao_w
from	pls_ocorrencia
where	nr_sequencia = nr_seq_ocorrencia_p;

select	max(ds_documentacao)
into STRICT	ds_ocor_document_w
from	pls_ocor_documentacao
where	nr_seq_ocorrencia	= nr_seq_ocorrencia_p;



if (ie_tipo_p = 'C') then
	ds_retorno_w := cd_ocorrencia_w;
elsif (ie_tipo_p = 'D') then
	ds_retorno_w := ds_ocorrencia_W;
elsif (ie_tipo_p = 'ME') then
	ds_retorno_w := ds_mensagem_externa_w;
elsif (ie_tipo_p = 'DO') then
	ds_retorno_w := replace(replace(ds_documentacao_w,Chr(13),''),Chr(10),' ');
elsif (ie_tipo_p = 'DE') then
	ds_retorno_w := ds_mensagem_externa_ww;
elsif (ie_tipo_p = 'DCT') then
	ds_retorno_w := substr(ds_ocor_document_w,1,255);
end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
