CREATE OR REPLACE FUNCTION pls_obter_dados_odonto (nr_seq_prestador_p bigint) RETURNS varchar AS $body$
DECLARE


ie_retorno  varchar(1) := 'N';
qt_registro bigint  := 0;


BEGIN
	IF (nr_seq_prestador_p IS NOT NULL AND nr_seq_prestador_p::text <> '') THEN
      SELECT COUNT(*)
      INTO STRICT qt_registro
      FROM pls_prestador a
      WHERE nr_sequencia = nr_seq_prestador_p
      AND EXISTS (SELECT coalesce(1,0)
		FROM pls_tipo_prestador p
		WHERE p.nr_sequencia = a.nr_seq_tipo_prestador
		AND ie_odontologico  = 'S');
    END IF;

    IF (qt_registro > 0) THEN
      ie_retorno   := 'S';
    END IF;

    return ie_retorno;
  END;
$body$
